DECLARE @Date VARCHAR(MAX)

SET @Date = '2014-01-31'

SELECT 
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 00:00:00.000' AND @Date + ' 01:00:00.000' THEN 1 ELSE NULL END) AS '12am - 1am', 
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 01:00:00.000' AND @Date + ' 02:00:00.000' THEN 1 ELSE NULL END) AS '1am - 2am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 02:00:00.000' AND @Date + ' 03:00:00.000' THEN 1 ELSE NULL END) AS '2am - 3am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 03:00:00.000' AND @Date + ' 04:00:00.000' THEN 1 ELSE NULL END) AS '3am - 4am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 04:00:00.000' AND @Date + ' 05:00:00.000' THEN 1 ELSE NULL END) AS '4am - 5am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 05:00:00.000' AND @Date + ' 06:00:00.000' THEN 1 ELSE NULL END) AS '5am - 6am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 06:00:00.000' AND @Date + ' 07:00:00.000' THEN 1 ELSE NULL END) AS '6am - 7am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 07:00:00.000' AND @Date + ' 08:00:00.000' THEN 1 ELSE NULL END) AS '7am - 8am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 08:00:00.000' AND @Date + ' 09:00:00.000' THEN 1 ELSE NULL END) AS '8am - 9am',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 09:00:00.000' AND @Date + ' 10:00:00.000' THEN 1 ELSE NULL END) AS '9am - 10am', 
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 10:00:00.000' AND @Date + ' 11:00:00.000' THEN 1 ELSE NULL END) AS '10am - 11am',
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 11:00:00.000' AND @Date + ' 12:00:00.000' THEN 1 ELSE NULL END) AS '11am - 12pm',
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 12:00:00.000' AND @Date + ' 13:00:00.000' THEN 1 ELSE NULL END) AS '12pm - 1am', 
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 13:00:00.000' AND @Date + ' 14:00:00.000' THEN 1 ELSE NULL END) AS '1pm - 2pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 14:00:00.000' AND @Date + ' 15:00:00.000' THEN 1 ELSE NULL END) AS '2pm - 3pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 15:00:00.000' AND @Date + ' 16:00:00.000' THEN 1 ELSE NULL END) AS '3pm - 4pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 16:00:00.000' AND @Date + ' 17:00:00.000' THEN 1 ELSE NULL END) AS '4pm - 5pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 17:00:00.000' AND @Date + ' 18:00:00.000' THEN 1 ELSE NULL END) AS '5pm - 6pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 18:00:00.000' AND @Date + ' 19:00:00.000' THEN 1 ELSE NULL END) AS '6pm - 7pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 19:00:00.000' AND @Date + ' 20:00:00.000' THEN 1 ELSE NULL END) AS '7pm - 8pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 20:00:00.000' AND @Date + ' 21:00:00.000' THEN 1 ELSE NULL END) AS '8pm - 9pm',  
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 21:00:00.000' AND @Date + ' 22:00:00.000' THEN 1 ELSE NULL END) AS '9pm - 10pm', 
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 22:00:00.000' AND @Date + ' 23:00:00.000' THEN 1 ELSE NULL END) AS '10pm - 11pm',
 COUNT(CASE WHEN StartDateTime BETWEEN @Date + ' 23:00:00.000' AND @Date + ' 23:59:59.999' THEN 1 ELSE NULL END) AS '11pm - 12am'
	FROM watsonTask